function PopupWithForm(props) {

  function closePopupEsp(e) {
    if(e.keyCode === 27) {
      console.log('1324')
    }
  }
  function one(evt) {
    React.useEffect(() => {
      evt.addEventListener('keydown', closePopupEsp)
      return () => {
        evt.removeEventListener('keydown', closePopupEsp)
      }
    })
  }


  return (
    <>
      <div className={`popup popup_type_${props.name} ${ props.isOpen ? "popup_opened" : "" }`} onClick={props.onClose} onclick={one}>
        <div className="popup__content" onClick={(e) => {e.stopPropagation()}}>
          <form className={`popup__form popup__form_${props.name}`} name={props.nameFrom} onSubmit={props.onSubmit}>
            <h2 className="popup__title">{props.title}</h2>
            {props.children}
            <button type="submit" className="popup__buttom popup__buttom_card popup__submit" >{props.buttonText}</button>
          </form>
          <button onClick={props.onClose} type="button" className="popup__buttom-close" />
        </div>
      </div>
      </>
  )
}

export default PopupWithForm;
