import React from 'react';
import {api}  from '../../utils/Api';



function Main(props) {

const[profileName, setProfileName] = React.useState('');
const[profileAbout, setprofileAbout] = React.useState('');
const[profileAvatar, setprofileAvatar] = React.useState('')
const[cards, setCards] = React.useState('')

React.useEffect(() => {
  api.getUserInfo()
  .then(res => {
    setProfileName(res.name)
    setprofileAbout(res.about)
    setprofileAvatar(res.avatar)
  })
  .catch((err) => {
    console.log(`Ошибка получения данных: ${err}`)
  })
})

React.useEffect(() => {
  api.getAllCards()
  .then(res => {
    setCards(res)
  })
  .catch((err) => {
    console.log(`Ошибка получения данных: ${err}`)
  })
})

  return (
    <main>
      <section className="profile">
        <div className="profile__image" style={{backgroundImage: `url(${profileAvatar})`}}>
          <button onClick={props.handleEditAvatarClick} type="button" className="profile__edit-avatar"></button>
        </div>
        <div className="profile__info" >
          <div className="profile__info-blocks">
            <h1 className="profile__title">
            {profileName}
            </h1>
            <button onClick={props.handleEditProfileClick} type="button" className="profile__edit-button" />
          </div>
          <p className="profile__subtitle">
          {profileAbout}
          </p>
        </div>
        <button onClick={props.handleAddPlaceClick} type="button" className="profile__button" />
      </section>
      <section className="elements">
      {cards.map((data, i) =>
      <div className="element">
      <img src={props.card.link} className="element__image" alt="Пользовательское изображение" onClick={handleClick} />
      <button id="" type="button" className="element__trash">
       <img src={Trash} className="element__trash-image"  alt="Иконка для удаления карточки" />
      </button>
      <div className="element__footer">
        <h2 className="element__place-name">{props.card.name}</h2>
        <div className="element__like-container">
         <button type="button" className="element__like"></button>
         <p className="element__like-counter">{props.card.likes.length}</p>
        </div>
      </div>
     </div>
          )}
      </section>
    </main>
  )
}

export default Main;
