import React from "react";
import {auth} from "../utils/Auth";

function Login (props) {

  const[email, setEmail] = React.useState('');
  const[password, setPassword] = React.useState('');

  console.log(email, password)
  function handleEmail(e) {
    setEmail(e.target.value)
  }

  function handlePassword(e) {
    setPassword(e.target.value)
  }

  function handleSubmit(e) {
    e.preventDefault();
    auth.authorization({
      password: password,
      email: email
    })
      .then((res) => {
        console.log("res")

      })
      .catch((err) => console.log("ошибка регистрации пользователя: " + err))
  }

  return (
    <div className="container">
      <h2 className="container__header">
        Вход
      </h2>
      <form className="form" onSubmit={handleSubmit}>
        <input className="form__input" name="email" type="email" placeholder="Email" required onChange={handleEmail}/>
        <input className="form__input" name="password" type="password" placeholder="Пароль" required onChange={handlePassword}/>
        <button className="form__submit-button" type="submit">
          Войти
        </button>
      </form>
    </div>
  );
};
export default Login;
