import React from 'react';
import Api from '../../utils/api';

const[profileName, setProfileName] = React.useState('')

Api.getUserInfo()
.then(res => {
  setProfileName(res.name)
})

function Main(props) {
  return (
    <main>
        <section className="profile">
          <button onClick={props.handleEditAvatarClick} type="button" className="profile__edit-avatar"><img src="#" className="profile__image" alt="Аватарка" /></button>
          <div className="profile__info" >
            <div className="profile__info-blocks">
              <h1 className="profile__title" />
              <button onClick={props.handleEditProfileClick} type="button" className="profile__edit-button" />
            </div>
            <p className="profile__subtitle" />
          </div>
          <button onClick={props.handleAddPlaceClick} type="button" className="profile__button" />
        </section>
        <section className="elements">
        </section>
      </main>
  )
}

export default Main;
