import React from 'react';
import {api}  from '../../utils/Api';



function Main(props) {

const[profileName, setProfileName] = React.useState('');
const[profileAbout, setprofileAbout] = React.useState('');
const[profileAvatar, setprofileAvatar] = React.useState('')
const[cards, setCards] = React.useState([])

React.useEffect(() => {
  api.getUserInfo()
  .then(res => {
    setProfileName(res.name)
    setprofileAbout(res.about)
    setprofileAvatar(res.avatar)
  })
  .catch((err) => {
    console.log(`Ошибка получения данных: ${err}`)
  })
},[])

React.useEffect(() => {
  api.getAllCards()
  .then(data => {
    console.log(data)
    setCards(data)
  })
  .catch((err) => {
    console.log(`Ошибка получения данных: ${err}`)
  })
},[])

  return (
    <main>
      <section className="profile">
        <div className="profile__image" style={{backgroundImage: `url(${profileAvatar})`}}>
          <button onClick={props.handleEditAvatarClick} type="button" className="profile__edit-avatar"></button>
        </div>
        <div className="profile__info" >
          <div className="profile__info-blocks">
            <h1 className="profile__title">
            {profileName}
            </h1>
            <button onClick={props.handleEditProfileClick} type="button" className="profile__edit-button" />
          </div>
          <p className="profile__subtitle">
          {profileAbout}
          </p>
        </div>
        <button onClick={props.handleAddPlaceClick} type="button" className="profile__button" />
      </section>
      <section className="elements">
      {cards.map((data,i) =>
        <div key={i} className="elements__group">
           <div className="elements__image" style={{backgroundImage: `url(${data.link})`}}></div>
        <button type="button" className="elements__button-delete"></button>
        <div className="elements__group-text">
          <h2 className="elements__title">{data.name}</h2>
          <div>
          <button type="button" className="elements__vector"></button>
          <div className="elements__count-likes">{data.likes.length}</div>
        </div>
        </div>
        </div>
      )}
      </section>
    </main>
  )
}

export default Main;
